import React from 'react';
import dataset from '../assets/dataset.json';
import * as CanvasJSReact from '../assets/canvasjs.react';
var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

class Chart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "Filler Title",
      axisX: {
        title: "Experiment",
        prefix: "$"
      },
      axisY: {
        title: "Amount of Inputs",
        prefix: "$"
      }, 
      toolTip: {
        shared: true
      },
      data: [],
      options: {}
    };

    this.toggleDataSeries = this.toggleDataSeries.bind(this);
  }

  updateTitle(output) {
    return output + " Chart";
  }

  compare(a, b) {
    return b[1] - a[1];
  }

  sortData(output) {
    let sorted = [];
    for (let exp in dataset) {
      sorted.push([exp, dataset[exp]["outputs"][output]]);
    }
    sorted.sort(this.compare);
    console.log("sortData", sorted);
    return sorted;
  }

  createDataPoints(input, output) {
    let sortedDataPoints = [];
    let i = 0;
    for (let exp of this.sortData(output)) {
      sortedDataPoints.push({
        x: i,
        y: dataset[exp[0]]["inputs"][input]
      })
      i++;
    }
    console.log("createDataPoints", sortedDataPoints)
    return {
      type: "stackedBar",
      name: input,
      showInLegend: "true",
      dataPoints: sortedDataPoints
    };
  }

  updateData(output) {
    console.log("updating data");
    let newData = [];
    for (let input of inputs) {
      newData.push(this.createDataPoints(input, output));
    }
    console.log("updateData", newData);
    this.setState({ data: newData });

    return newData;
  }

  toggleDataSeries(e) {
		if(typeof(e.dataSeries.visible) === "undefined" || e.dataSeries.visible) {
			e.dataSeries.visible = false;
		}
		else {
			e.dataSeries.visible = true;
		}
		this.chart.render();
	}

  updateOptions(output) {
    let newTitle = this.updateTitle(output);
    let newData = this.updateData(output);
    this.setState({ 
      options: {
        animationEnabled: true,
			  theme: "light2",
        title: {
          text: newTitle,
        },
        axisX: {
          title: "Experiment",
        },
        axisY: {
          title: "Amount of Inputs",
        }, 
        toolTip: {
          shared: true
        },
        legend:{
          cursor: "pointer",
          itemclick: this.toggleDataSeries
        },
        data: newData
      }
    });
  }

  componentDidUpdate() {
    console.log("options", this.state.options);
  }

  render() {
    return (
      <div className="chart">
        <button onClick={() => this.updateOptions("Viscosity")}>Viscosity</button>
        <button onClick={() => this.updateOptions("Cure Time")}>Cure Time</button>
        <button onClick={() => this.updateOptions("Elongation")}>Elongation</button>
        <button onClick={() => this.updateOptions("Tensile Strength")}>Tensile Strength</button>
        <button onClick={() => this.updateOptions("Compression Set")}>Compression Set</button>
        <CanvasJSChart options = { this.state.options } />
      </div>
    )
  }
}

export default Chart
