import React from 'react';
import dataset from '../assets/dataset.json';
import * as CanvasJSReact from '../assets/canvasjs.react';
var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

class Chart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      dataset: dataset,
      inputs: [
        "Polymer 1",
        "Polymer 2",
        "Polymer 3",
        "Polymer 4",
        "Carbon Black High Grade",
        "Carbon Black Low Grade",
        "Silica Filler 1",
        "Silica Filler 2",
        "Plasticizer 1",
        "Plasticizer 2",
        "Plasticizer 3",
        "Antioxidant",
        "Coloring Pigment",
        "Co-Agent 1",
        "Co-Agent 2",
        "Co-Agent 3",
        "Curing Agent 1",
        "Curing Agent 2",
        "Oven Temperature"
      ],
      title: "Filler Title",
      axisX: {
        title: "Experiment"
      },
      axisY: {
        title: "Amount of Inputs"
      }, 
      toolTip: {
        shared: true
      },
      options: {}
    };
  }

  updateTitle(output) {
    console.log("updating title");
    let newTitle = output + " Chart";
    this.setState({title: newTitle});
  }

  sortData(output) {
    // sort by descending based on the output for this 
    // sort by exp name in an array
    let sorted = [];
    for (let exp in this.state.dataset) {
      sorted.push((exp, this.state.dataset[exp]["outputs"][output]));
    }
    // now sort
    return sorted;
  }

  // x is the experiment name
  // y is the data point
  // but sorted by output

  createDataPoints(input) {
    let sortedDataPoints = [];
    for (let exp in this.state.dataset) {
      console.log(exp);
      sortedDataPoints.push({
        x: exp,
        y: this.state.dataset[exp]["inputs"][input]
      })
    }
    return {
      type: "stackedBar",
      name: input,
      dataPoints: sortedDataPoints
    };
  }

  updateData(output) {
    console.log("updating data");
    let newData = [];
    for (let input in this.state.inputs) {
      newData.push(this.createDataPoints(input));
    }
    this.setState({ data: newData });
  }

  updateOptions(output) {
    this.updateTitle(output);
    this.updateData(output);
    this.setState({ 
      options: {
        title: this.state.title,
        axisX: this.state.axisX,
        axisY: this.state.axisY,
        toolTip: this.state.toolTip,
        data: this.state.data
      } 
    })
  }

  render() {
    console.log(this.state.dataset);
    console.log(this.state.dataset["20170102_EXP_56"]);
    console.log(this.state.dataset["20170102_EXP_56"]["outputs"]);
    console.log(this.state.dataset["20170102_EXP_56"]["outputs"]["Viscosity"]);
    return (
      <div className="chart">
        <button onClick={() => this.updateOptions("Viscosity")}>Viscosity</button>
        <button onClick={() => this.updateOptions("Cure Time")}>Cure Time</button>
        <button onClick={() => this.updateOptions("Elongation")}>Elongation</button>
        <button onClick={() => this.updateOptions("Tensile Strength")}>Tensile Strength</button>
        <button onClick={() => this.updateOptions("Compression Set")}>Compression Set</button>
        <CanvasJSChart options = { this.state.options } />
      </div>
    )
  }
}

export default Chart
